svg.script:
  // MUOKKAA: Esittele talla rivilla muuttuja, jonka nimi on x
  var x;
  // MUOKKAA: Esittele talla rivilla muuttuja, jonka nimi on y
  var y;

  function main() {

    // MUOKKAA: Kysy x-muuttujalle arvo prompt-komennolla
	
	x = prompt("x-arvo");
    
	// MUOKKAA: Kysy y-muuttujalle arvo prompt-komennolla
	
	y = prompt("y-arvo");
	
    
	// Seuraava komento asettaa muuttujan x arvon "ympyra"-nimisen kuvion attribuuttiin cx
    
	document.getElementById("ympyra").setAttribute("cx", x);

    // Seuraava komento asettaa muuttujan y arvon "ympyra"-nimisen kuvion attribuuttiin cy
    
	document.getElementById("ympyra").setAttribute("cy", y);

    // Seuraava komento ajaa funktion main uudestaan 4000 millisekunnin odotuksen jalkeen
    
	setTimeout(main, 4000);
  }
